mapper.insert:
insert into member values (
SEQ_MEMBER.NEXTVAL
, #{memberId}
, #{memberPw}
, #{memberName}
, #{memberPhone}
, #{memberEmail}
, #{memberPostNum}
, #{memberAddr1}
, #{memberAddr2}
, 'Y'
, sysdate
, '0'
)

mapper.update:
update member set 
  M_NAME = #{memberName}
, M_PHONE = #{memberPhone}
, M_EMAIL = #{memberEmail}
, M_POST_NUM = #{memberPostNum}
, M_ADDRESS1 = #{memberAddr1}
, M_ADDRESS2 = #{memberAddr2}
where M_ID = #{memberId}

update member set 
	M_USER_ACTIVE = 'N'
	where M_ID = #{memberId}
