mapper.insert:



	SELECT SEQ_PRODUCT.CURRVAL FROM DUAL	



insert into product values(	
	SEQ_PRODUCT.NEXTVAL
	, #{productName}
	, #{productCategory}
	, #{productPrice}
	, #{productStock}
	, #{productContents}
	, #{productActive}
	, #{productBest}
	, #{productRegDate}
	, sysdate
)

mapper.select:
select * from ( select /*+ INDEX_DESC(p SYS_C0011286) */ rownum as rn , P_NO as productNo , P_NAME as productName , P_CATEGORY as productCategory , P_PRICE as productPrice , P_STOCK as productStock , P_CONTENTS as productContents , P_ACTIVE as productActive , P_BEST as productBest , P_REG_DATE as productRegDate , P_UPDATE_DATE as productUpdateDate from product p where
P_NAME like '%' || #{keyword} || '%' and
rownum <= #{pageNum} * #{amount} ) where rn > (#{pageNum} - 1) * #{amount}

mapper.update:

      	update product set
      		 P_NAME = #{productName}
			, P_CATEGORY = #{productCategory}
			, P_PRICE = #{productPrice}
			, P_STOCK = #{productStock}
			, P_CONTENTS = #{productContents}
			, P_ACTIVE = #{productActive}
			, P_BEST = #{productBest}
			, P_UPDATE_DATE = sysdate
			where P_NO = #{productNo}
